import{e as a}from"./app.dbec6f0d.js";import{_ as i}from"./plugin-vue_export-helper.21dcd24c.js";const e={},p=a('<h1 id="\u4E00\u3001\u9879\u76EE\u6982\u8FF0" tabindex="-1"><a class="header-anchor" href="#\u4E00\u3001\u9879\u76EE\u6982\u8FF0" aria-hidden="true">#</a> \u4E00\u3001\u9879\u76EE\u6982\u8FF0</h1><h2 id="_1-\u9879\u76EE\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#_1-\u9879\u76EE\u7B80\u4ECB" aria-hidden="true">#</a> 1. \u9879\u76EE\u7B80\u4ECB</h2><p>\u201C\u5C71\u897F\u5B66\u5B50\u664B\u6C90\u5357\u98CE\u8BA1\u5212\u201D\uFF0C\u662F\u7531\u5357\u4EAC\u5927\u5B66\u5728\u8BFB\u7684\u5C71\u897F\u5B66\u5B50\u53D1\u8D77\u5E76\u7EC4\u6210\u7684\u5927\u5B66\u751F\u652F\u6559\u56E2\u961F\uFF0C\u4F9D\u6258\u4E8E\u5357\u4EAC\u5927\u5B66\u9A6C\u514B\u601D\u4E3B\u4E49\u5B66\u9662\u7684\u5E08\u8D44\u6307\u5BFC\u4E0E\u7269\u8D28\u652F\u6301\uFF0C\u7ACB\u5FD7\u4E8E\u5BD2\u6691\u5047\u56DE\u5230\u5BB6\u4E61\u4E3A\u5C71\u897F\u7701\u5E7F\u7075\u53BF\u7684\u90E8\u5206\u5B66\u751F\u63D0\u4F9B\u6240\u9700\u8981\u7684\u5E2E\u52A9\uFF0C\u5305\u62EC\u4F46\u4E0D\u9650\u4E8E\u5B66\u4E60\u6307\u5BFC\uFF0C\u4E1A\u4F59\u751F\u6D3B\u65B9\u5F0F\u8BA1\u5212\u3002\u901A\u8FC7\u8D70\u8FDB\u5F53\u5730\u7684\u65B9\u5F0F\u6765\u4E86\u89E3\u8D2B\u56F0\u5730\u533A\u6559\u80B2\u60C5\u51B5\uFF0C\u5728\u652F\u6559\u7684\u540C\u65F6\u5BF9\u5E7F\u7075\u7684\u6587\u5316\u4EA7\u4E1A\u8FDB\u884C\u8C03\u7814\u4E0E\u5BA3\u4F20\uFF0C\u5C06\u6559\u80B2\u6276\u8D2B\u4E0E\u6587\u5316\u4EA7\u4E1A\u5F00\u53D1\u76F8\u7ED3\u5408\uFF0C\u4EE5\u8FBE\u5230\u7CBE\u51C6\u6276\u8D2B\u7684\u76EE\u6807\u3002</p><p>\u6559\u80B2\u8D44\u6E90\u7684\u57CE\u4E61\u5206\u914D\u4E25\u91CD\u4E0D\u5747\u8861\uFF0C\u8D2B\u56F0\u5730\u533A\u7684\u6559\u80B2\u8D44\u6E90\u65E0\u8BBA\u8F6F\u786C\u4EF6\u5747\u8FDC\u8FDC\u843D\u540E\u4E8E\u4E1C\u90E8\u53D1\u8FBE\u7701\u4EFD\u3002\u4F5C\u4E3A\u4ECE\u5C71\u897F\u8D70\u51FA\u53BB\u7684\u5E74\u8F7B\u4E00\u4EE3\uFF0C\u6211\u4EEC\u6DF1\u611F\u5730\u533A\u843D\u540E\u6240\u5BFC\u81F4\u7684\u6559\u80B2\u8D44\u6E90\u7F3A\u5C11\uFF0C\u5E0C\u671B\u5C3D\u81EA\u5DF1\u7684\u5FAE\u8584\u4E4B\u529B\u4E3A\u5BB6\u4E61\u505A\u4E9B\u4EC0\u4E48\u3002\u6211\u4EEC\u660E\u767D\uFF0C\u53EA\u6709\u8D2B\u56F0\u5730\u533A\u7684\u5B69\u5B50\u63A5\u53D7\u66F4\u52A0\u5168\u9762\u7684\u6559\u80B2\uFF0C\u63D0\u5347\u5176\u6587\u5316\u7D20\u517B\uFF0C\u624D\u80FD\u4E3A\u672A\u6765\u5BB6\u4E61\u7684\u8131\u8D2B\u81F4\u5BCC\u3001\u5C71\u897F\u7684\u6210\u529F\u8F6C\u578B\u63D0\u4F9B\u6E90\u6E90\u4E0D\u65AD\u7684\u4EBA\u529B\u8D44\u6E90\u4FDD\u969C\u3002</p><p>\u4E0E\u6B64\u540C\u65F6\uFF0C\u6211\u4EEC\u5E0C\u671B\u80FD\u591F\u5728\u7956\u56FD\u4E03\u5341\u534E\u8BDE\u4E4B\u9645\uFF0C\u5229\u7528\u6B64\u6B21\u6691\u671F\u8FD4\u4E61\u7684\u673A\u4F1A\uFF0C\u6DF1\u5165\u6316\u6398\u5BB6\u4E61\u7684\u6587\u5316\u5E95\u8574\u548C\u6587\u5316\u9057\u4EA7\uFF0C\u4E3A\u5BB6\u4E61\u6587\u5316\u9057\u4EA7\u7684\u4FDD\u62A4\u4E0E\u63A8\u5E7F\u732E\u8BA1\u732E\u7B56\u3002</p><p>\u5C71\u897F\u5386\u53F2\u6587\u5316\u5E95\u8574\u6DF1\u539A\uFF0C\u662F\u4E2D\u56FD\u5730\u4E0A\u6587\u7269\u6700\u591A\u7684\u7701\u4EFD\uFF0C\u4F46\u662F\u5BF9\u6587\u5316\u9057\u4EA7\u7684\u4FDD\u62A4\u3001\u5F00\u53D1\u548C\u5229\u7528\u5DE5\u4F5C\u505A\u5F97\u5C1A\u4E0D\u5230\u4F4D\u3002\u800C\u504F\u8FDC\u5730\u533A\u66F4\u662F\u7531\u4E8E\u8D44\u91D1\u4E25\u91CD\u4E0D\u8DB3\u3001\u4EA4\u901A\u4E0D\u4FBF\u3001\u914D\u5957\u8BBE\u65BD\u4E0D\u5B8C\u5584\uFF0C\u96BE\u4EE5\u8FDB\u884C\u6587\u5316\u9057\u4EA7\u7684\u4FDD\u62A4\u548C\u5F00\u53D1\u3002\u56E0\u6B64\u9996\u5148\u8981\u8FDB\u884C\u6DF1\u5165\u6316\u6398\uFF0C\u5BFB\u627E\u5C71\u897F\u6587\u5316\u9057\u4EA7\u80CC\u540E\u7684\u4E30\u5BCC\u5E95\u8574\uFF0C\u624D\u80FD\u6700\u5927\u7A0B\u5EA6\u5730\u5728\u4FDD\u62A4\u4F20\u7EDF\u6587\u5316\u7684\u57FA\u7840\u4E0A\u5B9E\u73B0\u6587\u5316\u8D44\u6E90\u5F00\u53D1\u548C\u5229\u7528\uFF0C\u6253\u597D\u6587\u5316\u724C\u3001\u65C5\u6E38\u724C\uFF0C\u63A8\u52A8\u5C71\u897F\u7684\u8F6C\u578B\u53D1\u5C55\u3002</p><p>\u800C\u4F5C\u4E3A\u5927\u5B66\u751F\u7684\u6211\u4EEC\uFF0C\u80FD\u505A\u5230\u7684\u4E5F\u4EC5\u6709\u53D1\u6325\u81EA\u5DF1\u7684\u70ED\u60C5\u4E0E\u4F18\u52BF\uFF0C\u5728\u5B66\u4E60\u65B9\u9762\u7ED9\u4E88\u4E2D\u5B66\u751F\u4E00\u4E9B\u6307\u5BFC\uFF0C\u7740\u773C\u4E8E\u6269\u5927\u4ED6\u4EEC\u7684\u773C\u754C\uFF0C\u4E3A\u4ED6\u4EEC\u6811\u7ACB\u6B63\u786E\u7684\u4EBA\u751F\u89C2\u4EF7\u503C\u89C2\u505A\u51FA\u5E94\u6709\u7684\u52AA\u529B\uFF1B\u5728\u4F20\u7EDF\u6587\u5316\u7684\u6DF1\u5165\u6316\u6398\u65B9\u9762\uFF0C\u8EAB\u4F53\u529B\u884C\u5730\u8FDB\u884C\u63A2\u8BBF\u3001\u8C03\u7814\u6D3B\u52A8\uFF0C\u5C24\u5176\u8981\u4E3A\u504F\u8FDC\u5730\u533A\u7684\u4F20\u7EDF\u6280\u827A\u3001\u4F20\u7EDF\u6587\u5316\u7684\u201C\u8D70\u51FA\u53BB\u201D\u505A\u51FA\u4E00\u70B9\u8D21\u732E\u3002</p><p>\u5728\u505A\u8FC7\u5927\u91CF\u8C03\u7814\u4E4B\u540E\uFF0C\u65B0\u95FB\u4F20\u64AD\u5B66\u9662 16 \u7EA7\u5C71\u897F\u7C4D\u5B66\u751F\u5F6D\u96C5\u7426\u7B49\u4EBA\u4E8E 2018 \u5E74\u53D1\u51FA\u4E86\u5C71\u897F\u5B66\u751F\u664B\u6C90\u5357\u98CE\u7684\u6D3B\u52A8\u5021\u8BAE\uFF0C\u5E76\u5F97\u5230\u4E86\u5357\u5927\u5B66\u5B50\u7684\u5E7F\u6CDB\u652F\u6301\u3002</p><p>2018 \u5E74\u662F\u664B\u6C90\u5357\u98CE\u652F\u6559\u7B2C\u4E00\u5E74\uFF0C\u7EFC\u5408\u8003\u8651\u591A\u79CD\u56E0\u7D20\u4E4B\u540E\uFF0C\u6211\u4EEC\u59D4\u5A49\u62D2\u7EDD\u4E86\u4ED6\u4E61\u5B66\u5B50\u7684\u62A5\u540D\u8BF7\u6C42\uFF0C\u5728\u51E0\u5341\u4F4D\u62A5\u540D\u7684\u5C71\u897F\u7C4D\u5B66\u751F\u4E2D\u6311\u9009\u51FA\u4E86 15 \u540D\u548C\u5021\u8BAE\u53D1\u8D77\u4EBA\u4E00\u8D77\u6784\u6210\u4E86\u664B\u6C90\u5357\u98CE\u56E2\u961F\uFF0C\u5B8C\u6210\u4E86\u4E3A\u671F 12 \u5929\u7684\u652F\u6559\u6D3B\u52A8\u3002</p><p>2019 \u5E74\uFF0C\u8303\u6587\u7433\u540C\u5B66\u63A5\u4EFB\u664B\u6C90\u5357\u98CE\u56E2\u961F\u4E3B\u5E2D\uFF0C\u7ECF\u8FC7\u91CD\u91CD\u9009\u62D4\uFF0C15 \u540D\u5B66\u5B50\u4E00\u8D77\u7EC4\u6210\u4E86\u65B0\u4E00\u5C4A\u7684\u664B\u6C90\u5357\u98CE\u56E2\u961F\uFF0C\u5E0C\u671B\u7EE7\u7EED\u4E3A\u5E7F\u7075\u7684\u53D1\u5C55\u8D21\u732E\u529B\u91CF\u3002</p><h2 id="_2-\u9879\u76EE\u80CC\u666F" tabindex="-1"><a class="header-anchor" href="#_2-\u9879\u76EE\u80CC\u666F" aria-hidden="true">#</a> 2. \u9879\u76EE\u80CC\u666F</h2><div class="custom-container tip"><p class="custom-container-title"></p><p>\u201C\u6CBB\u8D2B\u5148\u6CBB\u611A\u3002\u8981\u628A\u4E0B\u4E00\u4EE3\u7684\u6559\u80B2\u5DE5\u4F5C\u505A\u597D\uFF0C\u7279\u522B\u662F\u8981\u6CE8\u91CD\u5C71\u533A\u8D2B\u56F0\u5730\u533A\u4E0B\u4E00\u4EE3\u7684\u6210\u957F\uFF0C\u4E0B\u4E00\u4EE3\u8981\u8FC7\u4E0A\u597D\u751F\u6D3B\uFF0C\u9996\u5148\u8981\u6709\u6587\u5316\uFF0C\u8FD9\u6837\u5C06\u6765\u4ED6\u4EEC\u7684\u53D1\u5C55\u5C31\u5B8C\u5168\u4E0D\u540C\u3002\u4E49\u52A1\u6559\u80B2\u4E00\u5B9A\u8981\u641E\u597D\uFF0C\u8BA9\u5B69\u5B50\u4EEC\u53D7\u5230\u597D\u7684\u6559\u80B2\uFF0C\u4E0D\u8981\u8BA9\u5B69\u5B50\u4EEC\u8F93\u5728\u8D77\u8DD1\u7EBF\u4E0A\u3002\u53E4\u4EBA\u6709\u2018\u5BB6\u8D2B\u5B50\u8BFB\u4E66\u2019\u7684\u4F20\u7EDF\u3002\u628A\u8D2B\u56F0\u5730\u533A\u5B69\u5B50\u57F9\u517B\u51FA\u6765\uFF0C\u8FD9\u624D\u662F\u6839\u672C\u7684\u6276\u8D2B\u4E4B\u7B56\u3002\u201D \u2014\u2014\u4E60\u8FD1\u5E73</p></div><p>2012 \u5E74 12 \u6708 29\u300130 \u65E5\uFF0C\u603B\u4E66\u8BB0\u4E60\u8FD1\u5E73\u5728\u6CB3\u5317\u7701\u961C\u5E73\u53BF\u8003\u5BDF\u6276\u8D2B\u5F00\u53D1\u5DE5\u4F5C\u65F6\u66FE\u53D1\u8868\u8FC7\u5982\u4E0A\u7684\u8BB2\u8BDD\u3002</p><p>\u6559\u80B2\u6276\u8D2B\u4E00\u76F4\u662F\u6211\u56FD\u6276\u8D2B\u5DE5\u4F5C\u7684\u91CD\u8981\u7EC4\u6210\u90E8\u5206\uFF0C\u5176\u5728\u7CBE\u51C6\u6276\u8D2B\u4E2D\u5177\u6709\u5176\u4ED6\u6276\u8D2B\u65B9\u5F0F\u4E0D\u5177\u6709\u7684\u4F18\u52BF\u3002\u53EF\u4EE5\u8BF4\uFF0C\u6559\u80B2\u6276\u8D2B\u662F\u7CBE\u51C6\u6276\u8D2B\u7684\u6839\u672C\u4E4B\u7B56\u3002\u6211\u56FD\u6B63\u8D8A\u6765\u8D8A\u628A\u6559\u80B2\u7CBE\u51C6\u6276\u8D2B\u5F53\u4F5C\u5730\u65B9\u6276\u8D2B\u5DE5\u4F5C\u7684\u91CD\u70B9\uFF0C\u62D3\u5BBD\u6559\u80B2\u7CBE\u51C6\u6276\u8D2B\u8D44\u91D1\u6765\u6E90\u6E20\u9053\uFF0C\u63D0\u5347\u6559\u80B2\u7CBE\u51C6\u6276\u8D2B\u5B9E\u6548\u3002</p><p>\u5F53\u524D\uFF0C\u8D2B\u56F0\u5730\u533A\u843D\u540E\u7684\u6839\u672C\u539F\u56E0\uFF0C\u5E76\u4E0D\u662F\u56FD\u5BB6\u6276\u6301\u8D44\u91D1\u4E0D\u5230\u4F4D\uFF0C\u800C\u662F\u73B0\u6709\u4EBA\u529B\u8D44\u6E90\u77ED\u7F3A\u3002\u5357\u4EAC\u5927\u5B66\u5C71\u897F\u5B66\u5B50\u7262\u8BB0\u4E60\u603B\u4E66\u8BB0\u4E0D\u5FD8\u521D\u5FC3\u7684\u6559\u5BFC\uFF0C\u5E0C\u671B\u901A\u8FC7\u81EA\u5DF1\u7684\u52AA\u529B\u4FC3\u8FDB\u5BB6\u4E61\u5730\u533A\u7684\u6559\u80B2\u7CBE\u51C6\u6276\u8D2B\u3002</p><p>\u5341\u516B\u5927\u4EE5\u6765\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5C31\u5F3A\u8C03\u4E86\u6559\u80B2\u6276\u8D2B\u884C\u52A8\uFF0C\u6559\u80B2\u90E8\u4E5F\u8BA4\u771F\u5B66\u4E60\u8D2F\u5F7B\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5173\u4E8E\u6276\u8D2B\u5F00\u53D1\u7684\u91CD\u8981\u6218\u7565\u601D\u60F3\uFF0C\u7CBE\u51C6\u805A\u7126\u8D2B\u56F0\u5730\u533A\u7684\u6BCF\u4E00\u6240\u5B66\u6821\u3001\u6BCF\u4E00\u540D\u6559\u5E08\u3001\u6BCF\u4E00\u4E2A\u5B69\u5B50\uFF0C\u542F\u52A8\u5B9E\u65BD\u6559\u80B2\u6276\u8D2B\u5168\u8986\u76D6\u884C\u52A8\uFF0C\u5148\u540E\u7EC4\u7EC7\u5B9E\u65BD\u4E86 20 \u9879\u6559\u80B2\u60E0\u6C11\u653F\u7B56\u63AA\u65BD\uFF0C\u5B9E\u73B0\u4E86\u8D2B\u56F0\u5730\u533A\u4E49\u52A1\u6559\u80B2\u666E\u53CA\u3001\u5B66\u6821\u57FA\u7840\u8BBE\u65BD\u5EFA\u8BBE\u3001\u5B66\u751F\u8D44\u52A9\u4F53\u7CFB\u3001\u6559\u5E08\u961F\u4F0D\u5EFA\u8BBE\u3001\u6C11\u65CF\u6559\u80B2\u53D1\u5C55\u3001\u804C\u4E1A\u6559\u80B2\u63D0\u5347\u7B49\u9886\u57DF\u7684\u6559\u80B2\u6276\u8D2B\u5168\u65B9\u4F4D\u8986\u76D6\u3002</p><p>\u800C\u4E3A\u4E86\u8FDB\u4E00\u6B65\u5B8C\u5584\u4E2D\u56FD\u76EE\u524D\u6559\u80B2\u5B58\u5728\u7684\u4E0D\u516C\u5E73\u72B6\u51B5\uFF0C\u63A8\u8FDB 2020 \u5E74\u5168\u9762\u5C0F\u5EB7\u5EFA\u8BBE\uFF0C\u5341\u4E5D\u5927\u5BF9\u8D2B\u56F0\u5730\u533A\u8131\u8D2B\u653B\u575A\u63D0\u51FA\u4E86\u66F4\u9AD8\u7684\u8981\u6C42\u8BA4\u771F\u8D2F\u5F7B\u843D\u5B9E\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5728\u6DF1\u5EA6\u8D2B\u56F0\u5730\u533A\u8131\u8D2B\u653B\u575A\u5EA7\u8C08\u4F1A\u4E0A\u7684\u91CD\u8981\u8BB2\u8BDD\u7CBE\u795E\u548C\u300A\u4E2D\u5171\u4E2D\u592E\u529E\u516C\u5385\u3001\u56FD\u52A1\u9662\u529E\u516C\u5385\u5173\u4E8E\u652F\u6301\u6DF1\u5EA6\u8D2B\u56F0\u5730\u533A\u8131\u8D2B\u653B\u575A\u7684\u5B9E\u65BD\u610F\u89C1\u300B\u8981\u6C42\uFF0C\u5168\u56FD\u6559\u80B2\u7CFB\u7EDF\u66F4\u5E94\u8BE5\u4F1A\u540C\u6276\u8D2B\u7CFB\u7EDF\u63D0\u4F9B\u66F4\u52A0\u96C6\u4E2D\u7684\u652F\u6301\u3001\u66F4\u52A0\u6709\u6548\u7684\u4E3E\u63AA\u3001\u66F4\u52A0\u6709\u529B\u7684\u5DE5\u4F5C\uFF0C\u5171\u540C\u6253\u597D\u6DF1\u5EA6\u8D2B\u56F0\u5730\u533A\u6559\u80B2\u8131\u8D2B\u653B\u575A\u6218\u3002\u800C\u8FD9\u9700\u8981\u6240\u6709\u6559\u80B2\u76F8\u5173\u4EBA\u5458\u63D0\u9AD8\u601D\u60F3\u8BA4\u8BC6\u3001\u843D\u5B9E\u5DE5\u4F5C\u8D23\u4EFB\u3001\u8D2F\u5F7B\u4ECE\u4E25\u8981\u6C42\uFF0C\u6784\u5EFA\u957F\u6548\u673A\u5236\u3002</p><div class="custom-container tip"><p class="custom-container-title"></p><p>\u201C\u5386\u53F2\u6587\u5316\u662F\u57CE\u5E02\u7684\u7075\u9B42\uFF0C\u8981\u50CF\u7231\u60DC\u81EA\u5DF1\u7684\u751F\u547D\u4E00\u6837\u4FDD\u62A4\u597D\u5386\u53F2\u6587\u5316\u9057\u4EA7\u3002\u201D \u2014\u2014\u4E60\u8FD1\u5E73</p></div><p>2014 \u5E74 2 \u6708 25 \u65E5\uFF0C\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5728\u5317\u4EAC\u8003\u5BDF\u5DE5\u4F5C\u65F6\u8FD9\u6837\u8BB2\u3002</p><p>\u515A\u7684\u5341\u516B\u5927\u4EE5\u6765\uFF0C\u4EE5\u4E60\u8FD1\u5E73\u540C\u5FD7\u4E3A\u6838\u5FC3\u7684\u515A\u4E2D\u592E\uFF0C\u9AD8\u5EA6\u91CD\u89C6\u6587\u5316\u9057\u4EA7\u7684\u5386\u53F2\u610F\u4E49\u4E0E\u4F5C\u7528\uFF0C\u5C06\u5176\u4F5C\u4E3A\u65B0\u65F6\u671F\u6CBB\u56FD\u7406\u653F\u65B0\u7406\u5FF5\u65B0\u601D\u60F3\u65B0\u6218\u7565\u7684\u7EC4\u6210\u90E8\u5206\u3002\u515A\u7684\u5341\u4E5D\u5927\u5E84\u4E25\u5BA3\u544A\uFF0C\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u8FDB\u5165\u65B0\u65F6\u4EE3\uFF0C\u4E60\u8FD1\u5E73\u65B0\u65F6\u4EE3\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u601D\u60F3\u6210\u4E3A\u6211\u4EEC\u515A\u7684\u6307\u5BFC\u601D\u60F3\u3002\u5728\u4E60\u8FD1\u5E73\u65B0\u65F6\u4EE3\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u601D\u60F3\u4E2D\uFF0C\u5173\u4E8E\u6587\u5316\u9057\u4EA7\u7684\u601D\u60F3\u7406\u8BBA\u5360\u6709\u91CD\u8981\u4F4D\u7F6E\u3002</p><p>\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5173\u4E8E\u6587\u5316\u9057\u4EA7\u7684\u601D\u60F3\u7406\u8BBA\u535A\u5927\u7CBE\u6DF1\uFF0C\u5176\u4E2D\u65E2\u5305\u62EC\u4E2D\u534E\u4F18\u79C0\u6587\u5316\u4E2D\u7684\u7269\u8D28\u6587\u5316\u3001\u7CBE\u795E\u6587\u5316\u548C\u5236\u5EA6\u6587\u5316\uFF0C\u4E5F\u5305\u62EC\u4EBA\u7C7B\u6587\u660E\u6240\u521B\u9020\u7684\u4E00\u5207\u4F18\u79C0\u6587\u5316\u9057\u4EA7\u3002</p><p>\u4E60\u8FD1\u5E73\u603B\u4E66\u8BB0\u5728\u6B63\u5B9A\u5DE5\u4F5C\u671F\u95F4\u5C31\u66FE\u6307\u51FA\uFF1A\u201C\u6211\u4EEC\u4FDD\u7BA1\u4E0D\u597D\u6587\u7269\uFF0C\u5C31\u662F\u7F6A\u4EBA\uFF0C\u5C31\u4F1A\u6127\u5BF9\u540E\u4EBA\u3002\u201D\u5728\u798F\u5EFA\u5DE5\u4F5C\u65F6\uFF0C\u4ED6\u4E3A\u798F\u5DDE\u5E02\u5185\u7684\u540D\u4EBA\u6545\u5C45\u3001\u5386\u53F2\u5EFA\u7B51\u666E\u904D\u4F5C\u4E86\u653F\u5E9C\u6302\u724C\u4FDD\u62A4\uFF0C\u5E76\u6307\u51FA\u4FDD\u62A4\u5386\u53F2\u6587\u7269\u662F\u56FD\u5BB6\u6CD5\u5F8B\u8D4B\u4E88\u6BCF\u4E2A\u4EBA\u7684\u8D23\u4EFB\uFF0C\u4E5F\u662F\u5B9E\u65BD\u53EF\u6301\u7EED\u53D1\u5C55\u6218\u7565\u7684\u91CD\u8981\u5185\u5BB9\u3002</p><p>\u5728\u4E2D\u592E\u5DE5\u4F5C\u7279\u522B\u662F\u62C5\u4EFB\u603B\u4E66\u8BB0\u4EE5\u540E\uFF0C\u4ED6\u4ECD\u7136\u628A\u7269\u8D28\u6587\u5316\u9057\u4EA7\u7684\u4FDD\u62A4\u6302\u5728\u5FC3\u5934\u3002\u5728\u897F\u5B89\u8C03\u7814\u65F6\u4ED6\u6307\u51FA\uFF1A\u201C\u8981\u628A\u51DD\u7ED3\u7740\u4E2D\u534E\u6C11\u65CF\u4F20\u7EDF\u6587\u5316\u7684\u6587\u7269\u4FDD\u62A4\u597D\u3001\u7BA1\u7406\u597D\u3002\u201D2016 \u5E74\uFF0C\u4ED6\u53C8\u4F5C\u51FA\u4E86\u201C\u4FDD\u62A4\u6587\u7269\u529F\u5728\u5F53\u4EE3\u3001\u5229\u5728\u5343\u79CB\u201D\u7684\u91CD\u8981\u6279\u793A\uFF0C\u8981\u6C42\u5404\u7EA7\u515A\u59D4\u548C\u653F\u5E9C\u589E\u5F3A\u5BF9\u5386\u53F2\u6587\u7269\u7684\u656C\u754F\u4E4B\u5FC3\u3002\u515A\u7684\u5341\u4E5D\u5927\u5C06\u201C\u52A0\u5F3A\u6587\u7269\u4FDD\u62A4\u5229\u7528\u548C\u6587\u5316\u9057\u4EA7\u4FDD\u62A4\u4F20\u627F\u201D\u4F5C\u4E3A\u575A\u5B9A\u6587\u5316\u81EA\u4FE1\u7684\u4E00\u4E2A\u90E8\u5206\u5199\u8FDB\u62A5\u544A\u4E2D\uFF0C\u4F7F\u4E4B\u6210\u4E3A\u4E60\u8FD1\u5E73\u65B0\u65F6\u4EE3\u4E2D\u56FD\u7279\u8272\u793E\u4F1A\u4E3B\u4E49\u601D\u60F3\u7684\u7EC4\u6210\u90E8\u5206\u3002</p><p>\u5728\u6587\u5316\u9057\u4EA7\u7684\u4FDD\u62A4\u65B9\u9762\uFF0C\u6211\u4EEC\u6BCF\u4E2A\u4EBA\u90FD\u5E94\u5F53\u505A\u51FA\u4E00\u4EFD\u8D21\u732E\uFF0C\u6CD5\u5F8B\u8D4B\u4E88\u4E86\u6211\u4EEC\u4FDD\u62A4\u5386\u53F2\u6587\u7269\u7684\u8D23\u4EFB\uFF0C\u6211\u4EEC\u5C31\u5E94\u5F53\u5C3D\u5FC3\u5C3D\u529B\uFF0C\u4FDD\u62A4\u597D\u5386\u53F2\u6587\u7269\uFF0C\u4F20\u627F\u597D\u6587\u5316\u9057\u4EA7\uFF0C\u575A\u5B9A\u6587\u5316\u81EA\u4FE1\u3002</p><p>\u5C71\u897F\u5B66\u751F\u81EA\u613F\u53D1\u8D77\u7684\u5C71\u897F\u5B66\u5B50\u664B\u6C90\u5357\u98CE\u8BA1\u5212\uFF0C\u6B63\u662F\u7B26\u5408\u76EE\u524D\u8981\u6C42\u7684\uFF0C\u4EE5\u9A6C\u514B\u601D\u4E3B\u4E49\u5B66\u9662\u7275\u5934\uFF0C\u5C71\u897F\u5B66\u5B50\u5021\u5BFC\u53D1\u5C55\u7684\u957F\u671F\u9879\u76EE\uFF0C\u6709\u610F\u5728\u5C71\u897F\u7701\u5E7F\u7075\u53BF\u5EFA\u7ACB\u793E\u4F1A\u5B9E\u8DF5\u652F\u6559\u670D\u52A1\u57FA\u5730\u3002\u8FD9\u4E2A\u8FC7\u7A0B\u4E2D\uFF0C\u53EF\u4EE5\u5728\u5F88\u5927\u7A0B\u5EA6\u4E0A\u7F13\u89E3\u4EBA\u529B\u8D44\u6E90\u77ED\u7F3A\u7684\u95EE\u9898\uFF0C\u4E3A\u8D2B\u56F0\u5730\u533A\u7CBE\u51C6\u6276\u8D2B\u5DE5\u4F5C\u5F00\u5C55\u63D0\u4F9B\u66F4\u591A\u4EBA\u624D\u652F\u6301\uFF0C\u66F4\u597D\u7684\u843D\u5B9E\u6559\u80B2\u90E8\u5B9E\u73B0\u6559\u80B2\u6276\u8D2B\u7684\u5B9E\u65BD\u65B9\u6848\uFF0C\u63A8\u52A8\u5168\u9762\u5C0F\u5EB7\u7684\u5EFA\u8BBE\u3002\u4E0E\u6B64\u540C\u65F6\uFF0C\u53D1\u6398\u5F53\u5730\u5386\u53F2\u4F20\u7EDF\u6587\u5316\uFF0C\u505A\u597D\u6587\u5316\u9057\u4EA7\u7684\u7EE7\u627F\u4E0E\u4FDD\u62A4\u3002\u3002</p><h2 id="_3-\u9879\u76EE\u57FA\u7840" tabindex="-1"><a class="header-anchor" href="#_3-\u9879\u76EE\u57FA\u7840" aria-hidden="true">#</a> 3. \u9879\u76EE\u57FA\u7840</h2><h3 id="_3-1-\u5927\u91CF\u8C03\u7814\u94FA\u57AB" tabindex="-1"><a class="header-anchor" href="#_3-1-\u5927\u91CF\u8C03\u7814\u94FA\u57AB" aria-hidden="true">#</a> 3.1 \u5927\u91CF\u8C03\u7814\u94FA\u57AB</h3><p>\u5728\u5F00\u5C55\u664B\u6C90\u5357\u98CE\u6D3B\u52A8\u4E4B\u524D\uFF0C\u6211\u4EEC\u505A\u8FC7\u5927\u91CF\u7684\u8C03\u7814\u3002</p><h4 id="_3-1-1" tabindex="-1"><a class="header-anchor" href="#_3-1-1" aria-hidden="true">#</a> 3.1.1</h4><p>\u6211\u4EEC\u5728\u5357\u9752\u8BA1\u5212\u56DE\u6821\u5BA3\u8BB2\u65F6\uFF0C\u501F\u7528\u5BA3\u8BB2\u673A\u4F1A\u5BF9\u9AD8\u4E09\u5B66\u751F\u505A\u8FC7\u53CA\u65F6\u65E0\u8BB0\u540D\u8C03\u67E5\uFF0C\u5F97\u51FA\u5982\u4E0B\u7ED3\u8BBA\uFF1A</p><ul><li>\u8D2B\u56F0\u5730\u533A\u5B66\u6821\u7531\u4E8E\u56FD\u5BB6\u6559\u80B2\u7CBE\u51C6\u6276\u8D2B\uFF0C\u786C\u4EF6\u8BBE\u65BD\u76F8\u5BF9\u4E4B\u524D\u65E5\u6E10\u5B8C\u5584\uFF0C\u4F46\u6295\u5165\u4F7F\u7528\u529B\u5EA6\u4E0D\u591F\u5927\uFF0C\u786C\u4EF6\u8BBE\u65BD\u4F7F\u7528\u6548\u7387\u4E0D\u9AD8\u3002</li><li>\u8D2B\u56F0\u5730\u533A\u5B66\u6821\u66F4\u52A0\u5173\u6CE8\u5B66\u751F\u7684\u6210\u7EE9\uFF0C\u5BF9\u4E8E\u5176\u4EF7\u503C\u89C2\u548C\u4E2A\u4EBA\u6027\u683C\u65B9\u9762\u7684\u57F9\u517B\u7A0D\u6709\u6B20\u7F3A\uFF0C\u4F7F\u5F97\u5176\u5728\u9047\u5230\u632B\u6298\u65F6\u7684\u89E3\u538B\u80FD\u529B\u7A0D\u6709\u4E0D\u8DB3\uFF0C\u5BB9\u6613\u4EA7\u751F\u81EA\u5351\u653E\u5F03\u7B49\u5FC3\u7406\u3002</li><li>\u8D2B\u56F0\u5730\u533A\u5B66\u6821\u5728\u8BFE\u7A0B\u8BBE\u7F6E\u65F6\uFF0C\u66F4\u52A0\u5173\u6CE8\u9AD8\u8003\u79D1\u76EE\uFF0C\u4F8B\u5982\u8BED\u6570\u5916\u7406\u5316\u751F\uFF08\u653F\u53F2\u5730\uFF09\uFF0C\u5BF9\u4E8E\u4F53\u80B2\u3001\u97F3\u4E50\u8BFE\u7A0B\u7B49\u6295\u5165\u8F83\u5C11\u3002\u5B66\u751F\u65E0\u6CD5\u8FBE\u5230\u7EFC\u5408\u53D1\u5C55\u3002</li><li>\u53D7\u5730\u533A\u7ECF\u6D4E\u53D1\u5C55\u7B49\u9650\u5236\uFF0C\u5B66\u751F\u5728\u8BA1\u7B97\u673A\u3001\u7269\u7406\u3001\u5316\u5B66\u7B49\u57FA\u7840\u5B66\u79D1\u5174\u8DA3\u57F9\u517B\u6B20\u7F3A\uFF0C\u96BE\u4EE5\u771F\u6B63\u5728\u5927\u5B66\u9636\u6BB5\u878D\u5165\u57FA\u7840\u5B66\u79D1\u79D1\u7814\u6559\u5B66\u8BA1\u5212\uFF0C\u8F93\u5728\u8D77\u8DD1\u7EBF\u3002</li><li>\u5B66\u751F\u81EA\u8EAB\u5F3A\u70C8\u5E0C\u671B\u63A5\u89E6\u4E00\u4E9B\u5F53\u4EE3\u9752\u5E74\u5B66\u751F\u5FC5\u5907\u4F46\u5B66\u6821\u5E76\u4E0D\u5177\u5907\u5F00\u8BBE\u80FD\u529B\u7684\u8BFE\u7A0B\uFF0C\u6BD4\u5982\u65B0\u5A92\u4F53\u3001\u8BA1\u7B97\u673A\u8BBE\u8BA1\u7B49\u3002</li><li>\u5B66\u751F\u5E0C\u671B 985 \u9662\u6821\u5B66\u957F\u5B66\u59D0\u591A\u591A\u4F20\u6388\u4E00\u4E9B\u8003\u8BD5\u6280\u5DE7\uFF0C\u5B66\u4E60\u65B9\u6CD5\u7B49\uFF0C\u4EB2\u8EAB\u7ECF\u5386\u8FC7\u9AD8\u8003\u7684\u8003\u8BD5\u4EBA\u53EF\u80FD\u6BD4\u8001\u5E08\u5BF9\u9AD8\u8003\u7684\u95F4\u63A5\u4F53\u9A8C\u66F4\u6DF1\u3002</li></ul><h4 id="_3-1-2" tabindex="-1"><a class="header-anchor" href="#_3-1-2" aria-hidden="true">#</a> 3.1.2</h4><p>\u5728\u5357\u4EAC\u5927\u5B66\u53D1\u653E\u9762\u5411\u9AD8\u6821\u5B66\u751F\u7684\u8C03\u67E5\u95EE\u5377\uFF0C\u505A\u8FC7\u4E00\u4E9B\u5173\u4E8E\u8D2B\u56F0\u5730\u533A\u5B66\u751F\u7684\u95EE\u5377\u8C03\u67E5\u3002\u6211\u4EEC\u53D1\u653E\u7684\u95EE\u5377\u9898\u4E3A\u300A\u5173\u4E8E\u8D2B\u56F0\u53BF\u201C\u56FD\u5BB6\u4E13\u9879\u8BA1\u5212\u201D\u5165\u5B66\u5B66\u751F\u751F\u6D3B\u5B66\u4E60\u6EE1\u610F\u5EA6\u8C03\u67E5\u300B\uFF0C\u5728\u8C03\u67E5\u4E2D\u6211\u4EEC\u53D1\u73B0\uFF1A</p><ul><li>\u591A\u6570\u4E13\u9879\u5165\u5B66\u5B66\u751F\u5728\u9662\u7CFB\u6210\u7EE9\u6392\u540D\u8F83\u843D\u540E\u3002</li><li>\u591A\u6570\u4E13\u9879\u5165\u5B66\u5B66\u751F\u5165\u5B66\u540E\u4F1A\u56E0\u7EFC\u5408\u7D20\u8D28\u843D\u540E\u4ED6\u4EBA\u800C\u4EA7\u751F\u81EA\u5351\u3001\u538C\u5B66\u60C5\u7EEA\u3002</li><li>\u591A\u6570\u4E13\u9879\u5165\u5B66\u5B66\u751F\u4F1A\u56E0\u4E2A\u4EBA\u7279\u957F\u4E0D\u7A81\u51FA\uFF0C\u6D88\u6781\u53C2\u52A0\u793E\u56E2\u6D3B\u52A8\uFF0C\u53D8\u5F97\u6C89\u95F7\u538B\u6291\u3002</li></ul><h4 id="_3-1-3" tabindex="-1"><a class="header-anchor" href="#_3-1-3" aria-hidden="true">#</a> 3.1.3</h4><p>\u5728\u5C71\u897F\u7C4D\u5B66\u751F\u4E2D\u53D1\u653E\u8C03\u67E5\u95EE\u5377\uFF0C\u8FDB\u884C\u5BF9\u4E8E\u4E2D\u5B66\u9636\u6BB5\u6559\u80B2\u7684\u95EE\u5377\u8C03\u67E5\u3002\u6211\u4EEC\u53D1\u653E\u7684\u95EE\u5377\u9898\u4E3A\u300A\u5C71\u897F\u7701\u4E2D\u5B66\u751F\u62E9\u6821\u89C2\u5FF5\u8C03\u67E5\u300B\uFF0C\u5728\u8C03\u67E5\u4E2D\u6211\u4EEC\u53D1\u73B0\uFF1A</p><ul><li>\u5728\u9009\u62E9\u9AD8\u4E2D\u65F6\uFF0C\u5B66\u751F\u53CA\u5BB6\u957F\u66F4\u591A\u8003\u8651\u7684\u662F\u5B66\u6821\u7684\u5347\u5B66\u7387\uFF0C\u5176\u6B21\u662F\u6821\u56ED\u6587\u5316\u3002\u7531\u4E8E\u6559\u80B2\u8D44\u6E90\u4E0D\u5747\u8861\uFF0C\u4ED6\u4EEC\u4F1A\u5728\u653F\u7B56\u5141\u8BB8\u7684\u6761\u4EF6\u4E0B\u9009\u62E9\u8DE8\u533A\u5C31\u8BFB\u3002</li><li>\u591A\u6570\u5B66\u751F\u5BF9\u9AD8\u4E2D\u4E4B\u540E\u7684\u751F\u6DAF\u89C4\u5212\u8FD8\u505C\u7559\u5728\u7B80\u5355\u7684\u201C\u4E0A\u5927\u5B66\u201D\u4E0A\uFF0C\u4F46\u5BF9\u5177\u4F53\u7684\u65B9\u5411\u4ECD\u7136\u5F88\u8FF7\u832B\u3002</li><li>\u591A\u6570\u5B66\u751F\u5E0C\u671B\u5728\u4E2D\u5B66\u9636\u6BB5\u53EF\u4EE5\u638C\u63E1\u4EBA\u9645\u4EA4\u5F80\u7684\u6280\u5DE7\uFF0C\u4E86\u89E3\u66F4\u591A\u81EA\u7136\u79D1\u5B66\u548C\u4EBA\u6587\u5386\u53F2\u77E5\u8BC6\uFF0C\u5E0C\u671B\u53EF\u4EE5\u6709\u66F4\u591A\u953B\u70BC\u521B\u65B0\u80FD\u529B\u7684\u5B9E\u8DF5\u6027\u8BFE\u7A0B\uFF0C\u540C\u65F6\u5E0C\u671B\u6709\u771F\u6B63\u5B8C\u5584\u7684\u97F3\u4F53\u7F8E\u65B9\u9762\u8BFE\u7A0B\u3002</li><li>\u591A\u6570\u5B66\u751F\u8BB0\u5FC6\u4E2D\uFF0C\u5BF9\u81EA\u5DF1\u8D1F\u9762\u5F71\u54CD\u8F83\u5927\u7684\u8001\u5E08\u51FA\u73B0\u5728\u5C0F\u5B66\u53CA\u4EE5\u4E0B\uFF0C\u6B63\u9762\u5F71\u54CD\u8F83\u5927\u7684\u8001\u5E08\u51FA\u73B0\u5728\u4E2D\u5B66\u65F6\u671F\u3002</li></ul><h4 id="_3-1-4" tabindex="-1"><a class="header-anchor" href="#_3-1-4" aria-hidden="true">#</a> 3.1.4</h4><p>2017 \u5E74\u6691\u5047\uFF0C\u5357\u4EAC\u5927\u5B66\u7684\u5C71\u897F\u7EA7\u5B66\u5B50\u5F6D\u96C5\u7426\u7B49\u4EBA\u53C2\u4E0E\u5929\u6D25\u5927\u5B66\u201C\u7EC7\u68A6\u8BA1\u5212\u201D\uFF0C\u4EB2\u81EA\u53C2\u4E0E\u5E7F\u7075\u652F\u6559\u9879\u76EE\uFF0C\u5B8C\u5584\u5F53\u65F6\u5DF2\u5728\u8FDB\u884C\u4E2D\u7684\u8C03\u7814\u62A5\u544A\u3002</p><p>\u5929\u6D25\u5927\u5B66\u201C\u7EC7\u68A6\u8BA1\u5212\u201D\u56E2\u961F\u662F\u7EAF\u5FD7\u613F\u652F\u6559\u670D\u52A1\u7EC4\u7EC7\uFF0C\u4E0D\u4E0E\u5F53\u5730\u5B66\u6821\u5408\u4F5C\uFF0C\u800C\u4E0E\u793E\u533A\u5408\u4F5C\uFF0C\u6709\u6548\u5BF9\u63A5\u5F53\u5730\u5C45\u6C11\uFF0C\u63D0\u4F9B\u9488\u5BF9\u9AD8\u8003\u79D1\u76EE\u7684\u8F85\u5BFC\uFF0C2016 \u5438\u6536\u5B66\u751F\u4EBA\u6570 200 \u4EBA\u5DE6\u53F3\uFF0C2017 \u5E74\u5438\u6536\u5B66\u751F\u4EBA\u6570 100 \u4EBA\u5DE6\u53F3\u3002\u5728\u5438\u53D6\u5929\u6D25\u5927\u5B66\u7EC7\u68A6\u8BA1\u5212\u56E2\u961F\u7ECF\u9A8C\u6559\u8BAD\u7684\u57FA\u7840\u4E0A\uFF0C\u6211\u4EEC\u4FEE\u6539\u4E86\u201C\u664B\u6C90\u5357\u98CE\u201D\u8BA1\u5212\u7684\u9879\u76EE\u53D1\u5C55\u89C4\u5212\u3002</p><p>2018 \u5E74\uFF0C\u664B\u6C90\u5357\u98CE\u7B2C\u4E00\u5E74\uFF0C\u8D70\u8FDB\u4E86\u8212\u60E0\u793E\u533A\u548C\u5E7F\u7075\u4E94\u4E2D\uFF0C\u7ED3\u5408\u4E86\u5929\u6D25\u7EC7\u68A6\u7684\u793E\u533A\u57FA\u7840\u548C\u5357\u98CE\u7684\u5B66\u6821\u8D44\u6E90\uFF0C\u6DF1\u83B7\u5F53\u5730\u4EBA\u7684\u597D\u8BC4\u3002\u671F\u95F4\uFF0C\u591A\u6B21\u6709\u5B66\u751F\u53CA\u5BB6\u957F\u53D1\u6765\u79F0\u8D5E\u548C\u95EE\u5019\u3002</p><h4 id="_3-1-5" tabindex="-1"><a class="header-anchor" href="#_3-1-5" aria-hidden="true">#</a> 3.1.5</h4><p>\u67E5\u9605\u5F53\u5730\u53BF\u5FD7\uFF0C\u4E86\u89E3\u5F53\u5730\u5386\u53F2\u6587\u5316\u9057\u4EA7\uFF0C\u6211\u4EEC\u53D1\u73B0\uFF1A</p><ul><li>\u5E7F\u7075\u526A\u7EB8\uFF0C\u7F8E\u89C2\u3001\u7ECF\u6D4E\u3001\u9002\u7528\uFF0C\u4E0D\u4EC5\u4E3A\u5E7F\u5927\u4EBA\u6C11\u6240\u559C\u7231\uFF0C\u4E14\u6DF1\u53D7\u6CD5\u56FD\u3001\u6CE2\u5170\u548C\u65E5\u672C\u548C\u6E2F\u6FB3\u540C\u80DE\u8D5E\u8A89\u3002\u5E7F\u7075\u526A\u7EB8\u56FE\u6837\u7E41\u591A\uFF0C\u4EBA\u7269\u3001\u52A8\u7269\u3001\u690D\u7269\u4EE5\u53CA\u8868\u73B0\u751F\u6D3B\u3001\u8C61\u5F81\u5E78\u798F\u5409\u7965\u7684\u56FE\u6848\u65E0\u6240\u4E0D\u6709\uFF1B\u6784\u56FE\u9971\u6EE1\uFF0C\u5B8C\u6574\u8FDE\u8D2F\u3001\u5F62\u8C61\u751F\u52A8\uFF1B\u5200\u6CD5\u7EC6\u817B\uFF0C\u7EBF\u6761\u6D41\u7545\u3001\u5149\u6ED1\u6574\u9F50\u3002\u8FD9\u662F\u5E7F\u7075\u975E\u5E38\u91CD\u8981\u7684\u4E00\u5927\u6587\u5316\u7279\u8272\u3002 \u5176\u4EA7\u54C1\u8FDC\u9500\u7F8E\u56FD\u3001\u65E5\u672C\u3001\u52A0\u62FF\u5927\u7B49 20 \u591A\u4E2A\u56FD\u5BB6\u548C\u5730\u533A\uFF0C2009 \u5E74\u5C06\u5E7F\u7075\u526A\u7EB8\u5965\u5DF4\u9A6C\u8096\u50CF\u4F5C\u4E3A\u56FD\u793C\u8D60\u9001\u5230\u7F8E\u56FD\uFF0C2009 \u5E74\u5E7F\u7075\u526A\u7EB8\u548C\u9655\u897F\u3001\u6CB3\u5317\u7B49\u5404\u7701\uFF08\u5E02\u3001\u533A\uFF09\u5217\u5165\u56FD\u5BB6\u7EA7\u975E\u7269\u8D28\u6587\u5316\u9057\u4EA7\u4FDD\u62A4\u540D\u5F55\u7684\u526A\u7EB8\u9879\u76EE\u8054\u5408\u6253\u5305\uFF0C\u5217\u5165\u300A\u4EBA\u7C7B\u975E\u7269\u8D28\u6587\u5316\u9057\u4EA7\u4EE3\u8868\u4F5C\u540D\u5F55\u300B\u3002</li><li>\u5E7F\u7075\u53E4\u9057\u5740\u4F17\u591A\uFF0C\u6709\u8DDD\u4ECA\u4E24\u4E07\u591A\u5E74\u7684\u6D17\u9A6C\u5E84\u9057\u5740\uFF0C\u4E3A\u65E7\u65F6\u671F\u6587\u5316\u9057\u5740\uFF0C\u8FD8\u6709\u65B0\u77F3\u5668\u65F6\u4EE3\u3001\u5546\u5468\u65F6\u671F\u7684\u4F17\u591A\u53E4\u9057\u5740\u3002</li></ul><p>\u9664\u6B64\u4E4B\u5916\uFF0C\u5E7F\u7075\u8FD8\u4FDD\u7559\u4E86\u4F17\u591A\u53E4\u5893\u846C\u5EFA\u7B51\uFF0C\u5982\u57CE\u5173\u9547\u6C49\u5893\u846C\u3001\u5E73\u57CE\u6C49\u5893\u7FA4\u3001\u7389\u798F\u5C71\u91D1\u4EE3\u5893\u846C\u3002</p><p>\u53E6\u5916\uFF0C\u8FD8\u6709\u59CB\u5EFA\u4E8E\u5317\u9B4F\u65F6\u671F\u3001\u660E\u6E05\u5E74\u95F4\u91CD\u4FEE\u7684\u5723\u6CC9\u5BFA\u3001\u4EE5\u53CA\u660E\u6E05\u65F6\u671F\u7684\u6C34\u795E\u5802\u3001\u5723\u4F5B\u5BFA\u5893\u5854\u7B49\u53E4\u5EFA\u7B51\u3002\u5386\u53F2\u5EFA\u7B51\u4E30\u5BCC\u3002</p>',46);function r(h,d){return p}var n=i(e,[["render",r]]);export{n as default};
